import React, { useEffect, useRef } from 'react';

// Google Maps API script loading
const loadGoogleMapsScript = (callback) => {
  const existingScript = document.getElementById('googleMaps');
  const apiKey = process.env.REACT_APP_GMAPS_API_KEY;

  if (!existingScript && apiKey) {
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}`;
    script.id = 'googleMaps';
    document.body.appendChild(script);

    script.onload = () => {
      if (window.google && window.google.maps) {
        callback();
      } else {
        console.error('Google Maps API failed to load');
      }
    };

    script.onerror = () => {
      console.error('Error loading Google Maps API script');
    };
  } else if (existingScript) {
    if (window.google && window.google.maps) {
      callback();
    } else {
      existingScript.onload = () => {
        callback();
      };
    }
  } else {
    console.error('No API key found for Google Maps');
  }
};

const DashboardLandingPage = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    loadGoogleMapsScript(() => {
      const bangaloreBounds = {
        north: 13.15,  // Adjust this value to control the northern boundary
        south: 12.80,  // Adjust this value to control the southern boundary
        west: 77.45,   // Adjust this value to control the western boundary
        east: 77.75    // Adjust this value to control the eastern boundary
      };

      const bangalore = { lat: 12.9716, lng: 77.5946 };

      const map = new window.google.maps.Map(mapRef.current, {
        center: bangalore,
        zoom: 13,  // Adjust zoom level as needed
        disableDefaultUI: true,  // Hide all default controls and markers
        restriction: {
          latLngBounds: bangaloreBounds,
          strictBounds: true,  // Prevent panning outside the restricted area
        },
        styles: [
          {
            "elementType": "geometry",
            "stylers": [{ "color": "#1d2c4d" }]
          },
          {
            "elementType": "labels.text.fill",
            "stylers": [{ "color": "#8ec3b9" }]
          },
          {
            "elementType": "labels.text.stroke",
            "stylers": [{ "color": "#1a3646" }]
          },
          {
            "featureType": "administrative.country",
            "elementType": "geometry.stroke",
            "stylers": [{ "color": "#4b6878" }]
          },
          {
            "featureType": "administrative.land_parcel",
            "stylers": [{ "visibility": "off" }]
          },
          {
            "featureType": "administrative.locality",
            "elementType": "labels.text.fill",
            "stylers": [{ "color": "#c4d600" }]
          },
          {
            "featureType": "poi",
            "stylers": [{ "visibility": "off" }]  // Hide points of interest for cleaner UI
          },
          {
            "featureType": "road",
            "elementType": "geometry",
            "stylers": [{ "color": "#38414e" }]
          },
          {
            "featureType": "road",
            "elementType": "geometry.stroke",
            "stylers": [{ "color": "#212a37" }]
          },
          {
            "featureType": "road.highway",
            "elementType": "geometry",
            "stylers": [{ "color": "#746855" }]
          },
          {
            "featureType": "road.highway.controlled_access",
            "elementType": "geometry",
            "stylers": [{ "color": "#4e4e4e" }]
          },
          {
            "featureType": "water",
            "elementType": "geometry",
            "stylers": [{ "color": "#0e1626" }]
          }
        ]
      });
    });
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-tl from-bg1 via-bg2 to-bg1 text-white flex flex-col">
      <div ref={mapRef} style={{ width: '100%', height: '100vh' }} />
    </div>
  );
};

export default DashboardLandingPage;
