import React, { useEffect, useRef } from 'react';

// Google Maps API script loading
const loadGoogleMapsScript = (callback) => {
  const existingScript = document.getElementById('googleMaps');
  const apiKey = process.env.REACT_APP_GMAPS_API_KEY;

  if (!existingScript && apiKey) {
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}`;
    script.id = 'googleMaps';
    document.body.appendChild(script);

    script.onload = () => {
      if (window.google && window.google.maps) {
        callback();
      } else {
        console.error('Google Maps API failed to load');
      }
    };

    script.onerror = () => {
      console.error('Error loading Google Maps API script');
    };
  } else if (existingScript) {
    if (window.google && window.google.maps) {
      callback();
    } else {
      existingScript.onload = () => {
        callback();
      };
    }
  } else {
    console.error('No API key found for Google Maps');
  }
};

const DashboardLandingPage = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    loadGoogleMapsScript(() => {
      const bangaloreBounds = {
        north: 13.10,  // Adjust for northern bound of Bangalore
        south: 12.80,  // Adjust for southern bound of Bangalore
        west: 77.40,   // Adjust for western bound of Bangalore
        east: 77.80,   // Adjust for eastern bound of Bangalore
      };

      const bangalore = { lat: 12.9716, lng: 77.5946 };

      const map = new window.google.maps.Map(mapRef.current, {
        center: bangalore,
        zoom: 12,
        disableDefaultUI: true, // Disable all default controls
        restriction: {
          latLngBounds: bangaloreBounds,
          strictBounds: true,
        },
        styles: [
          {
            "featureType": "all",
            "elementType": "geometry",
            "stylers": [
              {
                "color": "#000000" // Black out everything by default
              }
            ]
          },
          {
            "featureType": "road",
            "elementType": "geometry",
            "stylers": [
              {
                "visibility": "off" // Remove roads outside the bounds
              }
            ]
          },
          {
            "featureType": "landscape",
            "elementType": "geometry",
            "stylers": [
              {
                "color": "#212121" // Dark color for landscape
              }
            ]
          },
          {
            "featureType": "poi",
            "elementType": "geometry",
            "stylers": [
              {
                "visibility": "off" // Hide points of interest outside bounds
              }
            ]
          },
          {
            "featureType": "administrative.locality",
            "elementType": "labels",
            "stylers": [
              {
                "visibility": "off" // Hide labels
              }
            ]
          },
          {
            "featureType": "administrative.country",
            "elementType": "labels",
            "stylers": [
              {
                "visibility": "off" // Hide country labels
              }
            ]
          },
          {
            "featureType": "administrative.province",
            "elementType": "labels",
            "stylers": [
              {
                "visibility": "off" // Hide state/province labels
              }
            ]
          },
          {
            "featureType": "administrative.land_parcel",
            "elementType": "labels",
            "stylers": [
              {
                "visibility": "off" // Hide land parcel labels
              }
            ]
          },
          {
            "featureType": "water",
            "elementType": "geometry",
            "stylers": [
              {
                "color": "#000000" // Black out water bodies outside bounds
              }
            ]
          },
          {
            "featureType": "landscape.man_made",
            "elementType": "geometry",
            "stylers": [
              {
                "visibility": "off" // Hide man-made landscapes
              }
            ]
          },
          {
            "featureType": "landscape.natural",
            "elementType": "geometry",
            "stylers": [
              {
                "color": "#212121" // Natural areas with dark color
              }
            ]
          }
        ] // Night mode styles and black out
      });
    });
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-tl from-bg1 via-bg2 to-bg1 text-white flex flex-col">
      <div ref={mapRef} style={{ width: '100%', height: '100vh' }} />
    </div>
  );
};

export default DashboardLandingPage;
